import React, { useEffect, useRef } from "react";
import io from 'socket.io-client';


const socket = io('http://localhost:2000/')

const Camera = () => {


  const videoRef = useRef(null);
  const getVideo = () => {
    socket.connect()
    navigator.mediaDevices
      .getUserMedia({ video: { width:  720} })
      .then(stream => {

        let video = videoRef.current;
        video.srcObject = stream;
        video.play();
        // socket.emit('stream', stream);


        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        // set the canvas width and height to match the video dimensions
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        setInterval(() => {
          // draw the current video frame onto the canvas
          ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
          // get the image data from the canvas
          const imageData = canvas.toDataURL()
          console.log(imageData)
          // send the image data over the socket connection as a binary string
          socket.emit('stream', imageData, { binary: true });
        }, 1000 / 30); // send 30 frames per second






      })
      .catch(err => {
        console.error("error:", err);
      });
    socket.on('output', (data) => {console.log(data)}) 
  };

    useEffect(() => {
    getVideo();
  }, [videoRef]);



  return (
    <div>
      <div>

        <video ref={videoRef} />
        <h1> Server </h1>
      </div>
    </div>
  );

};

export default Camera;
