        };
        sendFrame();



      })
      .catch(err => {
        console.error("error:", err);
      });


  };

    useEffect(() => {
    getVideo();
  }, [inputRef]);


  useEffect(() => {
    let output = outputRef.current;
    const updateOutput = () => {
      // console.log('how many times');
      if(videoOutput != null) {  output.src = videoOutput;}

      window.requestAnimationFrame(updateOutput);
    };
    updateOutput();
  }, [videoOutput]);

  return (
    <div>
      <div>
        <h1> Client </h1>
        <video ref={inputRef} />
        <h1> Server </h1>
        <img alt='sever' ref={outputRef} /> 
      </div>
    </div>
  );

};

