import React, { useState, useEffect } from 'react';
import socketio from 'socket.io-client';

const Webcam = () => {
  const [stream, setStream] = useState(null);
  const [error, setError] = useState(null);
  const [socket, setSocket] = useState(null);
  const [videoDisplay, setVideoDisplay] = useState(null)
  const [webcamEnabled, setWebcamEnabled] = useState(false);

  const handleClick = () => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then(stream => {
        setStream(stream);
        setWebcamEnabled(true);
      })
      .catch(error => {
        setError(error);
      });
  }

  useEffect(() => {
    setVideoDisplay(URL.createObjectURL(stream))
  }, [stream])

  // useEffect(() => {
  //   if (webcamEnabled && stream) {
  //     const socket = socketio.connect('http://localhost:2000');

  //     socket.on('connect', () => {
  //       console.log('Connected to the server');
  //     });

  //     const sendData = () => {
  //       socket.emit('stream', stream);
  //     };

  //     const intervalId = setInterval(sendData, 1000); // send data every second

  //     setSocket(socket);

  //     return () => {
  //       clearInterval(intervalId);
  //       socket.disconnect();
  //     };
  //   }
  // }, [webcamEnabled, stream]);

  return (
    <div>
      {/* <h1> Hello world</h1> */}
      {error && <p>{error.message}</p>}
      {webcamEnabled ? (
        <video
          src={videoDisplay}
          autoPlay
          style={{ width: '100%', height: '100%' }}
        />
      ) : (
        <button onClick={handleClick}>Access Webcam</button>
      )}
    </div>
  );
};

export default Webcam;
